   ];    }    return self;}- (void)viewDidLoad{    [super viewDidLoad];            self.navigationItem.rightBarButtonItem = self.editButtonItem;    [self.tableView registerClass:[UITableViewCell class] forCellReuseIdentifier:cellFileItme];    [self.tableView registerClass:[UITableViewCell class] forCellReuseIdentifier:kViewKey];}- (void)didReceiveMemoryWarning{        [super didReceiveMemoryWarning];  }#pragma mark - Table view data source-(CGFloat)tableView:(UITableView *)tableView heightForRowAtIndexPath:(NSIndexPath *)indexPath{        return ((indexPath.row==0) ? 60 : 38);}- (NSInteger)numberOfSectionsInTableView:(UITableView *)tableView{    return self.textFieldArray.count;}- (NSInteger)tableView:(UITableView *)tableView numberOfRowsInSection:(NSInteger)section{    return 2;}- (UITableViewCell *)tableView:(UITableView *)tableView cellForRowAtIndexPath:(NSIndexPath *)indexPath{    UITableViewCell *cell=nil;    if (indexPath.row==0) {        cell=[tableView dequeueReusableCellWithIdentifier:cellFileItme forIndexPath:indexPath];        UIView *view=[cell.contentView viewWithTag:1001];        if (view) {            [view removeFromSuperview];        }        [cell.contentView addSubview:[self.textFieldArray[indexPath.section] objectForKey:kViewKey]];    }else{        cell=[tableView dequeueReusableCellWithIdentifier:kViewKey forIndexPath:indexPath];        cell.textLabel.numberOfLines=2;        cell.textLabel.textAlignment=NSTextAlignmentCenter;        cell.textLabel.font=[UIFont systemFontOfSize:12];                cell.textLabel.text=[self.textFieldArray[indexPath.section] objectForKey:kExplainKey];            }        cell.selectionStyle=UITableViewCellSelectionStyleNone;        return cell;}-(NSString*)tableView:(UITableView *)tableView titleForHeaderInSection:(NSInteger)section{    return [self.textFieldArray[section] objectForKey:kTitleKey];}#pragma mark --#pragma mark UITEXTFIELD-(UITextField *)textFieldNormal{    if (_textFieldNormal==nil) {                CGRect frame= CGRectMake(10, 10, 300, 40);                UITextField *textFileZX =[[UITextField alloc]initWithFrame:frame];        textFileZX.tag=1001;        textFileZX.borderStyle=UITextBorderStyleNone;        textFileZX.returnKeyType=UIReturnKeyDone;        textFileZX.autocorrectionType=UITextAutocorrectionTypeNo;        textFileZX.placeholder =@"<enter text>";                _textFieldNormal=textFileZX;    }    return _textFieldNormal;}-(UITextField *)textFieldRounded{    if (_textFieldRounded==nil) {                CGRect frame= CGRectMake(10, 10, 300, 40);                UITextField *textFileZX =[[UITextField alloc]initWithFrame:frame];        textFileZX.tag=1001;        textFileZX.autocorrectionType=UITextAutocorrectionTypeNo;                textFileZX.returnKeyType = UIReturnKeyDone;                textFileZX.borderStyle=UITextBorderStyleRoundedRect;                textFileZX.placeholder =@"<enter text>";                _textFieldRounded=textFileZX;    }    return _textFieldRounded;}-(UITextField *)textFieldSecure{    if (_textFieldSecure==nil) {                CGRect frame= CGRectMake(10, 10, 300, 40);                UITextField *textFileZX =[[UITextField alloc]initWithFrame:frame];        textFileZX.tag=1001;        textFileZX.borderStyle=UITextBorderStyleBezel;                textFileZX.returnKeyType = UIReturnKeyDone;                textFileZX.placeholder =@"<enter passwd>";                textFileZX.secureTextEntry = YES;                _textFieldSecure=textFileZX;            }    return _textFieldSecure;}-(UITextField *)textFieldLeftView{    if (_textFieldLeftView==nil) {                CGRect frame= CGRectMake(10, 10, 300, 40);                UITextField *textFileZX =[[UITextField alloc]initWithFrame:frame];                textFileZX.tag=1001;                textFileZX.borderStyle=UITextBorderStyleLine;                textFileZX.placeholder =@"<enter text>";                textFileZX.leftView =[[UIImageView alloc]initWithImage:[UIImage imageNamed:@"segment_check"]];        textFileZX.leftViewMode=UITextFieldViewModeAlways;                textFileZX.returnKeyType =UIReturnKeyDone;        _textFieldLeftView=textFileZX;    }    return _textFieldLeftView;}@end