ieldArray[indexPath.section] objectForKey:kExplainKey];            }        cell.selectionStyle=UITableViewCellSelectionStyleNone;        return cell;}-(NSString*)tableView:(UITableView *)tableView titleForHeaderInSection:(NSInteger)section{    return [self.textFieldArray[section] objectForKey:kTitleKey];}#pragma mark --#pragma mark UITEXTFIELD-(UITextField *)uiTextField{    if (_uiTextField==nil) {                CGRect frame= CGRectMake(10, 10, 300, 40);                UITextField *textFileZX =[[UITextField alloc]initWithFrame:frame];                textFileZX.borderStyle=UITextBorderStyleRoundedRect;        textFileZX.placeholder =@"<enter text>";                _uiTextField=textFileZX;    }    return _uiTextField;}@end