pageCTL",                              kViewKey:self.pageCTL},                                                        @{kTitleKey:@"UIPAPECONTROLLER",                              kLabelKey:@"pageCTL",                              kExplainKey:@"viewTableViewController.m\n pageCTL",                              kViewKey:self.styleGray},                                                        @{kTitleKey:@"UIPROGRESSVIEW",                              kLabelKey:@"progressCTL",                              kExplainKey:@"viewTableViewController.m\n progressCTL",                              kViewKey:self.progressCTL},                                                        @{kTitleKey:@"UISTEPPER",                              kLabelKey:@"stepperCTL",                              kExplainKey:@"viewTableViewController.m\n stepperCTL",                              kViewKey:self.stepperCTL},                         ];    }    return self;}- (void)viewDidLoad{    [super viewDidLoad];        // Uncomment the following line to preserve selection between presentations.     self.clearsSelectionOnViewWillAppear = NO;        // Uncomment the following line to display an Edit button in the navigation bar for this view controller.     self.navigationItem.rightBarButtonItem = self.editButtonItem;    [self.tableView registerClass:[UITableViewCell class] forCellReuseIdentifier:cellFileItme];    [self.tableView registerClass:[UITableViewCell class] forCellReuseIdentifier:kTitleKey];}- (void)didReceiveMemoryWarning{    [super didReceiveMemoryWarning];    // Dispose of any resources that can be recreated.}#pragma mark - Table view data source-(CGFloat)tableView:(UITableView *)tableView heightForRowAtIndexPath:(NSIndexPath *)indexPath{    return ((indexPath.row==0) ? 48 :38);}- (NSInteger)numberOfSectionsInTableView:(UITableView *)tableView{    // Return the number of sections.    return self.viewArray.count;}- (NSInteger)tableView:(UITableView *)tableView numberOfRowsInSection:(NSInteger)section{    // Return the number of rows in the section.    return 2;}-(NSString *)tableView:(UITableView *)tableView titleForHeaderInSection:(NSInteger)section{    return [self.viewArray[section] objectForKey:kTitleKey];    }- (UITableViewCell *)tableView:(UITableView *)tableView cellForRowAtIndexPath:(NSIndexPath *)indexPath{    UITableViewCell *cell =nil  ;    if (indexPath.row==0) {        cell=[tableView dequeueReusableCellWithIdentifier:cellFileItme forIndexPath:indexPath];        UIView  *viewZX=[cell.contentView viewWithTag:1001];        if (viewZX) {            [viewZX removeFromSuperview  ];        }                cell.textLabel.text=[self.viewArray[indexPath.section] objectForKey:kLabelKey];        cell.textLabel.font=[UIFont fontWithName:@"Symbol" size:23];//        cell.textLabel.textAlignment =NSTextAlignmentCenter;        cell.accessoryView=[self.viewArray[indexPath.section] objectForKey:kViewKey];                if (viewZX == ((UIView *)self.styleGray)) {            [self.styleGray startAnimating];        }            }else{        cell=[tableView dequeueReusableCellWithIdentifier:kTitleKey forIndexPath:indexPath];        cell.textLabel.text=[self.viewArray[indexPath.section] objectForKey:kExplainKey];        cell.textLabel.numberOfLines=2;        cell.textLabel.textAlignment=NSTextAlignmentCenter;        cell.textLabel.font=[UIFont systemFontOfSize:13];    }    cell.selectionStyle=UITableViewCellSelectionStyleNone;        return cell;}#pragma mark - controls---(UISwitch*)switchView{    if (_switchView==nil) {        UISwitch *viewZX=[[UISwitch alloc]init];        viewZX.tag=1001;        _switchView=viewZX;    }        return _switchView;}-(UISlider*)sliderview{    if (_sliderview==nil) {        UISlider *viewZX=[[UISlider alloc]init];         viewZX.tag=1001;        viewZX.value=0.5;                _sliderview=viewZX;    }        return _sliderview;}-(UISlider*)customizeSlider{    if (_customizeSlider==nil) {        UISlider *viewZX=[[UISlider alloc]init];         viewZX.tag=1001;        viewZX.value=0.4;        UIImage  *yellows=[[UIImage imageNamed:@"yellowslide"]                           resizableImageWithCapInsets:UIEdgeInsetsMake(0, 10, 0,10)                           resizingMode:UIImageResizingModeStretch];        UIImage  *oranges=[[UIImage imageNamed:@"orangeslide"]                           resizableImageWithCapInsets:UIEdgeInsetsMake(0, 10, 0,10)                           resizingMode:UIImageResizingModeStretch];                [viewZX setMaximumTrackImage:yellows forState:UIControlStateNormal];        [viewZX setMinimumTrackImage:oranges forState:UIControlStateNormal];        [viewZX setThumbImage:[UIImage imageNamed:@"slider_ball"] forState:UIControlStateNormal];        _customizeSlider=viewZX;    }        return _customizeSlider;}-(UIPageControl*)pageCTL{    if (_pageCTL==nil) {        UIPageControl *viewZX=[[UIPageControl alloc]initWithFrame:CGRectMake(0, 0, 150, 40)];         viewZX.tag=1001;        viewZX.backgroundColor=[UIColor grayColor];        viewZX.numberOfPages=10;        _pageCTL=viewZX;    }        return _pageCTL;}-(UIActivityIndicatorView*)styleGray{    if (_styleGray==nil) {        UIActivityIndicatorView *viewZX=[[UIActivityIndicatorView alloc]                                         initWithActivityIndicatorStyle:UIActivityIndicatorViewStyleGray ];        viewZX.frame=CGRectMake(0, 0, 40, 40);         viewZX.tag=1001;        viewZX.backgroundColor=[UIColor grayColor];        viewZX.hidesWhenStopped=NO;        [viewZX sizeToFit];                [viewZX startAnimating];        _styleGray = viewZX;    }        return _styleGray;}-(UIProgressView*)progressCTL{    if (_progressCTL==nil) {        UIProgressView *viewZX=[[UIProgressView alloc]initWithFrame:CGRectMake(0, 0, 150, 40)];         viewZX.tag=1001;        viewZX.progressViewStyle=UIProgressViewStyleDefault;        viewZX.backgroundColor=[UIColor redColor];        viewZX.progress=0.3;        _progressCTL=viewZX;    }        return _progressCTL;}-(UIStepper *)stepperCTL{    if (_stepperCTL==nil) {        UIStepper *viewZX=[[UIStepper alloc]init];         viewZX.tag=1001;        _stepperCTL=viewZX;    }        return _stepperCTL;}@end