alloc]initWithFrame:CGRectMake(20, 0, 80, 40)];        lable.text=self.title ;        [cell.contentView addSubview:lable   ];                CGRect frame= CGRectMake(10, 10, CGRectGetWidth(cell.contentView.frame)-20, 40);                UISwitch *textFileZX =[[UISwitch alloc]initWithFrame:frame];        //        textFileZX.borderStyle=UITextBorderStyleRoundedRect;        cell.accessoryView=textFileZX;//        [cell.contentView addSubview:textFileZX];            }else{                cell.textLabel.text=[self.viewArray[indexPath.section] objectForKey:kExplainKey];            }        return cell;}@end